  KEY `attribute_id` (`attribute_id`) USING BTREE,
  KEY `product_id` (`product_id`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb3;


DROP TABLE IF EXISTS `oc_ocfilter_cache`;
CREATE TABLE `oc_ocfilter_cache` (
  `key` bigint(20) unsigned NOT NULL DEFAULT 0,
  `value` longtext NOT NULL,
  `path` varchar(128) DEFAULT NULL,
  `expire` int(11) NOT NULL,
  PRIMARY KEY (`key`) USING BTREE,
  KEY `path` (`path`) USING BTREE,
  KEY `expire` (`expire`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb3;


DROP TABLE IF EXISTS `oc_ocfilter_filter`;
CREATE TABLE `oc_ocfilter_filter` (
  `filter_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `source` tinyint(1) unsigned NOT NULL DEFAULT 0,
  `type` set('checkbox','radio','slide','slide_dual') NOT NULL DEFAULT 'checkbox',
  `dropdown` tinyint(1) NOT NULL DEFAULT 0,
  `color` tinyint(1) NOT NULL DEFAULT 0,
  `image` tinyint(1) NOT NULL DEFAULT 0,
  `status` tinyint(1) NOT NULL DEFAULT 0,
  `sort_order` int(11) NOT NULL DEFAULT 0,
  PRIMARY KEY (`filter_id`,`source`) USING BTREE,
  KEY `sort_order` (`sort_order`) USING BTREE,
