DROP TABLE IF EXISTS `oc_ocfilter_filter_description`;
CREATE TABLE `oc_ocfilter_filter_description` (
  `filter_id` int(11) unsigned NOT NULL,
  `source` tinyint(1) unsigned NOT NULL DEFAULT 0,
  `language_id` tinyint(3) unsigned NOT NULL DEFAULT 0,
  `name` varchar(128) NOT NULL DEFAULT '',
  `suffix` varchar(32) NOT NULL DEFAULT '',
  `description` varchar(255) NOT NULL DEFAULT '',
  PRIMARY KEY (`filter_id`,`language_id`,`source`) USING BTREE,
  KEY `language_id` (`language_id`) USING BTREE,
  KEY `name` (`name`) USING BTREE
