  `max` decimal(15,3) DEFAULT NULL,
  PRIMARY KEY (`filter_id`,`source`,`product_id`) USING BTREE,
  KEY `product_id` (`product_id`) USING BTREE,
  KEY `min_max` (`min`,`max`) USING BTREE
