DROP TABLE IF EXISTS `oc_ocfilter_filter_to_category`;
CREATE TABLE `oc_ocfilter_filter_to_category` (
  `filter_id` int(11) unsigned NOT NULL,
  `source` tinyint(1) unsigned NOT NULL DEFAULT 0,
  `category_id` int(11) unsigned NOT NULL,
  PRIMARY KEY (`category_id`,`filter_id`,`source`) USING BTREE,
  KEY `category_id` (`category_id`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb3;


DROP TABLE IF EXISTS `oc_ocfilter_filter_to_store`;
CREATE TABLE `oc_ocfilter_filter_to_store` (
  `filter_id` int(11) unsigned NOT NULL,
  `source` tinyint(1) unsigned NOT NULL DEFAULT 0,
  `store_id` int(11) NOT NULL,
  PRIMARY KEY (`store_id`,`filter_id`,`source`) USING BTREE,
  KEY `store_id` (`store_id`) USING BTREE
